function uncompressTiles(arr) {  
  var newArr = [];
  var i=0;
  while (i<arr.length)
  {
    if (arr[i] < 0)
    {
      var num = Math.abs(arr[i]);
      var data = arr[i + 1];
      for (var j=0; j<num; j++)
      {
        newArr.push(data);
      }
      i += 2;
    }
    else
    {
      var num = arr[i];
      for (var j=0; j<num; j++)
      {
        i++;
        newArr.push(arr[i]);
      }
      i++;
    }
  }
  return newArr;
};
